import * as posenet from '@tensorflow-models/posenet';
import * as tf from '@tensorflow/tfjs';


const GRAVITY = 2.2;
const BOUNCYNESS = 0.97;
const BALL_COLORS = ['#d26f78', '#7c6daa', '#d55f98', '#a52361', '#b956b7', '#9176b7'];

function scaleBetween(unscaledNum, minAllowed, maxAllowed, min, max){
    return (maxAllowed - minAllowed) * (unscaledNum - min) / (max - min) + minAllowed;
}

function getDistance(x0, y0, x1, y1) {
    const x = x1 - x0;
    const y = y1 - y0;
    return Math.sqrt(x * x + y * y);
}

/*------------------------------*\
|* Main Canvas
\*------------------------------*/

class Canvas {
    constructor() {
        // setup a canvas
        this.canvas = document.getElementById('canvas');
        this.dpr = window.devicePixelRatio || 1;
        this.ctx = this.canvas.getContext('2d');
        this.ctx.scale(this.dpr, this.dpr);

        this.floor = window.innerHeight * this.dpr - window.innerHeight / 15 * this.dpr;

        this.setCanvasSize = this.setCanvasSize.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.render = this.render.bind(this);

        this.setCanvasSize();
        this.setupListeners();

        // create a ball this context
        this.balls = [];

        const amount = 20;
        for (let i = 0; i < amount; i++) {
            const ball = new Ball(this, getRandomInt(this.canvas.width / 40, this.canvas.width / 60));
            this.balls.push(ball);
        }

        this.render();   
    }

    setupListeners() {
        window.addEventListener('resize', this.setCanvasSize);
        window.addEventListener('click', this.handleClick);
    }

    handleClick(event) {
        const x = event.clientX;
        const y = event.clientY;
        this.balls.forEach(ball => {
            ball.bounceY(getDistance(x, y, ball.x, ball.y) / 50 + 20); 
        });
    }

    setCanvasSize() {
        this.canvas.width = window.innerWidth * this.dpr;
        this.canvas.height = window.innerHeight * this.dpr;
        this.canvas.style.width = window.innerWidth + 'px';
        this.canvas.style.height = window.innerHeight + 'px';
    }

    drawBackground() {
        const gradient = this.ctx.createLinearGradient(0, this.canvas.height, 0, 0);
        gradient.addColorStop(0, '#f9e873');
        gradient.addColorStop(1, '#fda873');
        this.ctx.fillStyle = gradient;
        this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height); 
        const size = this.canvas.width / 14;
        this.ctx.font = `bold ${size}px Futura`;
        this.ctx.textAlign = 'center';
        this.ctx.fillStyle = 'white';
        this.ctx.fillText('BOUNCY BALLS', this.canvas.width / 2, this.canvas.height / 2);
    }


    render() {
        this.drawBackground();

        // show ground
        // this.ctx.fillStyle = '#f6f893';
        // this.ctx.fillRect(0, this.floor, this.canvas.width, this.canvas.height + this.canvas.height - this.floor);
        this.balls.forEach(ball => {
            ball.draw();
            ball.updatePosition();
        });

        window.requestAnimationFrame(this.render);
    }
}

/*------------------------------*\
|* Ball Class
\*------------------------------*/

class Ball {
    constructor(canvas, size) {
        this.canvas = canvas;
        this.color = BALL_COLORS[Math.floor(BALL_COLORS.length * Math.random())];

        // radius axis
        this.ry = size * canvas.dpr;
        this.rx = size * canvas.dpr;

        // position
        this.x = window.innerWidth / 2 * canvas.dpr;
        this.y = window.innerHeight / 2 * canvas.dpr;

        // velocity
        this.vx = getRandomInt(-20, -10);
        this.vy = getRandomInt(-20, -50);

        // deltas
        this.dx = 0;
        this.dy = 0;

        this.distFromGround = this.canvas.floor - this.x;

        this.setupShadow();
    }

    updatePosition() {

        const floorOffset = this.canvas.floor - this.ry; // adjusted for ball radius
        const nextY = this.y + GRAVITY + this.vy;
        const nextX = this.x + this.vx;

        // floor check
        if (nextY >= floorOffset) {
            this.y = floorOffset;
            this.dy = 0;
            this.bounceY();
        } else {
            this.vy = GRAVITY + this.vy;
            this.dy = Math.abs(nextY - this.y);
            this.y = nextY;
        }

        // wall check
        if (nextX >= this.canvas.canvas.width - this.rx ||
            nextX <= this.rx) {
            this.dx = 0;
            this.bounceX();
        } else {
            this.dx = Math.abs(nextX - this.x);
            this.x = nextX;
        }


        // total distance
        const distFromGround = this.canvas.floor - this.y - this.ry;
        const distMax = this.canvas.canvas.height - this.canvas.canvas.height + this.canvas.floor;
        const distCapped = clamp(distFromGround, 0, distMax);

        // set shadow properties
        this.shadowOpacity = scaleBetween(distCapped, 1, 0, 0, distMax);
        this.shadowWidth = scaleBetween(distCapped, 0.9, 6, 0, distMax);

        this.stretchY = scaleBetween(this.dy, 0.99, 1.03, 0, 10);
    }

    bounceY(vy) {
        this.vy = vy ? vy : -this.vy * BOUNCYNESS;
        this.y += this.vy;
    }

    bounceX() {
        this.vx = -this.vx * BOUNCYNESS;
        this.x += this.vx;
    }

    setupShadow() {
        this.shadowCanvas = document.createElement('canvas');
        this.shadowCtx = this.shadowCanvas.getContext('2d');

        this.shadowCanvas.width = this.rx;
        this.shadowCanvas.height = this.ry;

        const gradient = this.shadowCtx.createRadialGradient(this.rx / 2, this.ry / 2, this.rx / 2, this.rx / 2, this.ry / 2, 0);
        gradient.addColorStop(1, 'rgba(69, 19, 144, 0.15)');
        gradient.addColorStop(0, 'rgba(69, 19, 144, 0)');
        this.shadowCtx.fillStyle = gradient;
        this.shadowCtx.fillRect(0, 0, this.rx, this.ry);
    }

    drawShadow() {
        const width = this.rx * this.shadowWidth * this.canvas.dpr;
        const x = this.x - width / 2;
        const height = this.ry / 1.5 * this.canvas.dpr;
        const y = this.canvas.floor - height / 2;

        this.canvas.ctx.globalAlpha = this.shadowOpacity;
        this.canvas.ctx.drawImage(this.shadowCanvas, x, y, width, height);
        // this.canvas.ctx.fillRect(x, y, width, height);
        this.canvas.ctx.globalAlpha = 1;
    }

    draw() {
        const gradient = this.canvas.ctx.createRadialGradient(this.x, this.y, this.rx * 2, this.x - this.rx * 2, this.y - this.ry * 2, 0);
        gradient.addColorStop(1, '#fdb873');
        gradient.addColorStop(0, this.color);
        this.canvas.ctx.fillStyle = gradient;


        this.drawShadow();
        this.canvas.ctx.fillStyle = gradient;
        // this.canvas.ctx.fillStyle = '#fdfeef';
        this.canvas.ctx.lineWidth = 4 * this.canvas.dpr;
        // this.canvas.ctx.strokeStyle = '#f9e873';
        // circle
        this.canvas.ctx.beginPath();
        this.canvas.ctx.ellipse(this.x, this.y, this.rx, this.ry * this.stretchY, 0, 0, 2 * Math.PI);
        // this.canvas.ctx.arc(this.x, this.y, this.rx, 0, 2 * Math.PI);
        this.canvas.ctx.fill();
        // this.canvas.ctx.stroke();
    }
}

function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function clamp(number, min, max) {
  return Math.max(min, Math.min(number, max));
}

const canvas = new Canvas();
